sourcesContent:
import { getConnection } from "./../database/ConexionDB";


const addPrueba = async (req, res) => {
    try {
        const { idprueba, pruebacol } = req.body;

        if (idprueba === undefined || pruebacol === undefined) {
            res.status(400).json({ message: "Bad Request. Please fill all field." });
        }

        const connection = await getConnection();
        await connection.query(`INSERT INTO prueba VALUES (${idprueba}, '${pruebacol}')`);
        res.status(200).json({ message: "Prueba added" });
    } catch (error) {
        res.status(500);
        res.send(error.message);
    }
};

export const methods = {
    addPrueba
};

